import { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      description: '',
      price: '',
      image: ''
    }
  }

  handleOnChange = e => {
    this.setState({ [e.target.name]: e.target.value, isEditable: true });
  };

  test = () => {
    console.log(this.state);
  }

  render() {
    return (
      <form name='form'>
        <header className="container">
          <div className='row'>
            <div className='col'>
              <div className="form-group">
                <input id="name" className="form-control txt" maxLength="100" placeholder="Product name"
                  value={this.state.name} onChange={this.handleOnChange} />
                <textarea id="description" className="form-control txt" rows="5" maxLength="1000" placeholder="Product desctiption"
                  value={this.state.description} onChange={this.handleOnChange} />
                <input type="text" id="price" className="form-control txt" placeholder="Product price"
                  value={this.state.price} onChange={this.handleOnChange} />
                <input type="file" id="image" className="form-control txt" placeholder="Image"
                  value={this.state.image} onChange={this.handleOnChange} />
                <button id="name" className='form-control btn primary-btn' onClick={this.test}>Add</button>
              </div>
            </div>
            <div className='col'>
              <h2>List of products</h2>
            </div>
          </div>


        </header>
      </form>
    );
  }
}

export default App;
